import { LOGIN } from "../action/type";

const initialState = ''
export const loginReducer = (state = initialState, action)=>{
    
    const {type, payload} = action;
    switch(type){
        case LOGIN :
            localStorage.setItem('token',payload.token)
            localStorage.setItem('username',payload.userName)
            return payload
        default:
            return state
    }
}
