import { SHOW_EMPLOYEE_DETAIL } from "../action/type";

const initialState = {}
export const employeeReducer = (state = initialState,action)=>{
    const {type, payload} = action;
    switch(type){
        case SHOW_EMPLOYEE_DETAIL:
            return { ...payload } 
        default:
            return state
    }
}
