import request from '../request'
const token = localStorage.getItem('token')
console.log(token);
const detail = () => {
 return request.get('/employee/detail',{
    headers:{
        'Authorization':`Bearer ${token}`
    }
}).data
}
const employeeService = {
    detail
}
export default employeeService
