import loginService from "../service/loginService";

import { LOGIN, CHANGE_PASSWORD, ERROR_MESSAGE } from "./type";
export const login = (value)=> async(dispatch)=>{
  
         await loginService.login(value).then((data)=>{
            dispatch({
                type : LOGIN,
                payload : data
            })
        }).catch(()=>{
            console.log(error);
            return dispatch({
                type : ERROR_MESSAGE,
                payload : error.response.data
            })
        })
    
   
       
  
}
