import { useNavigate } from "react-router-dom";
import loginService from "../service/loginService";

import { LOGIN, CHANGE_PASSWORD, ERROR_MESSAGE } from "./type";

export const login = (value)=> async(dispatch)=>{
    const navigate = useNavigate()
    try {
        const res = await loginService.login(value).then(()=>{
            navigate('/employee')
        })
      return dispatch({
            type : LOGIN,
            payload : res.data
        })
    } catch (error) {
        // console.log(error);
      return dispatch({
            type : ERROR_MESSAGE,
            payload : error.response.data
        })
    }
}
